import React, { useState } from "react";
import { FormLayout, Form, TextField, Button, Card, Page, Icon } from "@shopify/polaris";
import { useForm, Controller, ErrorMessage } from "react-hook-form";
import * as yup from "yup";
import ListOption from "../../ListOption";
import DataTableRule from "../../DataTable";
import { PlusCircleIcon } from '@shopify/polaris-icons';

export interface Props {
  name: string;
  label: string;
}

export interface rule {
  title: string;
  subtitle?: string;
  label?: string;
  quantity: number;
  type: string;
  amount?: number;
}

const initData = {
  title: "",
  subtitle: "",
  label: "",
  quantity: 0,
  type: "0",
  amount: 0
}

const WrappedTextFiled: React.FC<Props> = ({ label, name }) => {
  const [data, setData] = useState<Array<rule>>([
    {
      title: "Single",
      subtitle: "Standard price",
      label: "",
      quantity: 1,
      type: "0",
      amount: 0
    },
    {
      title: "Dou",
      subtitle: "Save 10%",
      label: "Popular",
      quantity: 2,
      type: "1",
      amount: 0
    }
  ]);

  const SignupSchema = yup.object().shape({
    campaign: yup.string().required(),
  });

  const onSubmit = (data: any) => {
    alert(JSON.stringify(data));
  };

  const defaultValues = {
    campaign: "",
    title: '',
    description: ''
  };

  const { register, handleSubmit, errors, control, formState } = useForm({
    defaultValues,
    validationSchema: SignupSchema,
    mode: 'onChange',
    reValidateMode: 'onSubmit',
    validateCriteriaMode: 'firstError'
  });

  const handleSave = (event: any) => {
    console.log(event);
  };

  const addOption = () => {
    setData([...data, {
      ...initData,
      quantity: parseInt(data[data.length - 1].quantity.toString()) + 1
    }])
  }

  const updateData = (index: number, key: keyof rule, value: any) => {
    const newData: rule[] = [...data];
    newData[index][key] = value;
    setData(newData);
  }

  const removeOption = (index: number) => {
    const newData: rule[] = [...data];
    newData.splice(index, 1);
    setData(newData);
  }

  console.log('form s', formState)

  return (
    <Page breadcrumbs={[{ url: "#" }]}
      title="Create volume discount"
      primaryAction={{
        content: "Save",
        disabled: false,
        onAction: handleSave
      }}>
      <div style={{ display: 'flex' }}>
        <div style={{ marginRight: 16 }}>
          <Card sectioned>
            <Form
              onSubmit={handleSubmit(onSubmit)}
            >
              <FormLayout>
                <h2>
                  General
                </h2>
                <Controller
                  as={
                    <TextField
                      label={'Campaign'}
                      onChange={([value]) => value}
                      error={errors && errors.campaign && errors.campaign.message}
                    />
                  }
                  onChange={([value]) => {
                    console.log('in change', value, errors)
                    console.log('form state is', formState)
                    return value
                  }}
                  name={"campaign"}
                  control={control}
                />
                <Controller
                  as={
                    <TextField
                      label={'Title'}
                      onChange={([value]) => value}
                      error={errors && errors.title && errors.title.message}
                    />
                  }
                  onChange={([value]) => {
                    console.log('in change', value, errors)
                    return value
                  }}
                  name={"title"}
                  control={control}
                />
                <Controller
                  as={
                    <TextField
                      label={'Description'}
                      onChange={([value]) => value}
                      error={errors && errors.description && errors.description.message}
                    />
                  }
                  onChange={([value]) => {
                    console.log('in change', value, errors)
                    console.log('form state is', formState)
                    return value
                  }}
                  name={"description"}
                  control={control}
                />
              </FormLayout>

            </Form>
          </Card>

          <Card sectioned>
            <h2>
              Volume discount rule
            </h2>
            {
              data.map((item, index) => {
                return (
                  <ListOption updateData={updateData} removeOption={removeOption} data={item} index={index.toString()} key={'option rule ' + index} />
                )
              })
            }

            <div className="flexCenter">
              <Button primary onClick={addOption}>
                <Icon source={PlusCircleIcon} />
                Add option</Button>
            </div>
          </Card>
        </div>

        <div>
          <h2>
            Preview
          </h2>
          <h2>Buy more and save</h2>
          <p>Apply for all products store</p>
          <DataTableRule data={data} />
        </div>

      </div>
    </Page>
  );
};

export default WrappedTextFiled;
