import React, { useState } from "react";
import { TextField, Button, Stack, FormLayout } from "@shopify/polaris";
import { useForm, Controller, ErrorMessage } from "react-hook-form";
import * as yup from "yup";

export interface Props {
  name: string;
  label: string;
}

const WrappedTextFiled: React.FC<Props> = ({ label, name }) => {
  const [data, setData] = useState(null);

  const SignupSchema = yup.object().shape({
    name: yup.string().required().max(3),
    pass: yup.string().required().max(4),
  });

  const onSubmit = (data: any) => {
    alert(JSON.stringify(data));
  };

  const defaultValues = {
    name: "12312312312",
    pass: 'abc123123123123'
  };

  const { register, handleSubmit, errors, control, formState } = useForm({
    defaultValues,
    validationSchema: SignupSchema,
    mode: 'onChange',
    reValidateMode: 'onSubmit',
    validateCriteriaMode: 'firstError'
  });

  console.log('form s', formState)

  return (
    <>
      <form
        onSubmit={handleSubmit(onSubmit)}
      >
        <FormLayout>
          <Controller
            as={
              <TextField
                label={label}
                onChange={([value]) => value}
                error={errors && errors.name && errors.name.message}
              />
            }
            onChange={([value]) => {
              console.log('in change', value, errors)
              console.log('form state is', formState)
              return value
            }}
            name={"name"}
            control={control}
          />
          <Controller
            as={
              <TextField
                label={label}
                onChange={([value]) => value}
                error={errors && errors.pass && errors.pass.message}
              />
            }
            onChange={([value]) => {
              console.log('in change', value, errors)
              return value
            }}
            name={"pass"}
            control={control}
          />
          <Button onClick={handleSubmit(onSubmit)}>submit</Button>
        </FormLayout>

      </form></>
  );
};

export default WrappedTextFiled;
