import React, { useState } from "react";
import { FormLayout, Form, TextField, Button, Card, Page } from "@shopify/polaris";
import { useForm, Controller, ErrorMessage } from "react-hook-form";
import * as yup from "yup";
import ListOption from "../../ListOption";

export interface Props {
  name: string;
  label: string;
}

export interface rule {
  title: string;
  subtitle?: string;
  label?: string;
  quantity: string;
  type: string;
  amount?: string;
}

const WrappedTextFiled: React.FC<Props> = ({ label, name }) => {
  const [data, setData] = useState<Array<rule>>([
    {
      title: "Volume discount rule",
      subtitle: "Buy 10 or more",
      label: "Quantity",
      quantity: "10",
      type: "0",
      amount: "0"
    }
  ]);

  const SignupSchema = yup.object().shape({
    campaign: yup.string().required(),
  });

  const onSubmit = (data: any) => {
    alert(JSON.stringify(data));
  };

  const defaultValues = {
    campaign: "",
    title: '',
    description: ''
  };

  const { register, handleSubmit, errors, control, formState } = useForm({
    defaultValues,
    validationSchema: SignupSchema,
    mode: 'onChange',
    reValidateMode: 'onSubmit',
    validateCriteriaMode: 'firstError'
  });

  const handleSave = (event: any) => {
    console.log(event);
  };

  console.log('form s', formState)

  return (
    <Page breadcrumbs={[{ url: "#" }]}
      title="Create volume discount"
      primaryAction={{
        content: "Save",
        disabled: false,
        onAction: handleSave
      }}>
      <Card sectioned>
        <Form
          onSubmit={handleSubmit(onSubmit)}
        >
          <FormLayout>
            <h2>
              General
            </h2>
            <Controller
              as={
                <TextField
                  label={'Campaign'}
                  onChange={([value]) => value}
                  error={errors && errors.campaign && errors.campaign.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                console.log('form state is', formState)
                return value
              }}
              name={"campaign"}
              control={control}
            />
            <Controller
              as={
                <TextField
                  label={'Title'}
                  onChange={([value]) => value}
                  error={errors && errors.title && errors.title.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                return value
              }}
              name={"title"}
              control={control}
            />
            <Controller
              as={
                <TextField
                  label={'Description'}
                  onChange={([value]) => value}
                  error={errors && errors.description && errors.description.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                console.log('form state is', formState)
                return value
              }}
              name={"description"}
              control={control}
            />
          </FormLayout>

        </Form>
      </Card>

      <Card sectioned>
        <h2>
          Volume discount rule
        </h2>
        <ListOption quantity={"1"} index="12" />
      </Card>
    </Page>
  );
};

export default WrappedTextFiled;
