import React, { useState } from "react";
import { FormLayout, Form, TextField, Button, Card, Page, Icon } from "@shopify/polaris";
import { useForm, Controller, ErrorMessage } from "react-hook-form";
import * as yup from "yup";
import {
  DeleteIcon
} from '@shopify/polaris-icons';

export interface Props {
  quantity: string;
  index: string;
}

const ListOption: React.FC<Props> = ({ quantity, index }) => {
  const [data, setData] = useState(null);

  const SignupSchema = yup.object().shape({
    campaign: yup.string().required(),
  });

  const onSubmit = (data: any) => {
    alert(JSON.stringify(data));
  };

  const defaultValues = {
    quantity: "",
    title: '',
    // description: ''
  };

  const { register, handleSubmit, errors, control, formState } = useForm({
    defaultValues,
    validationSchema: SignupSchema,
    mode: 'onChange',
    reValidateMode: 'onSubmit',
    validateCriteriaMode: 'firstError'
  });

  return (
      <div style={{borderTop: '1px solid'}}>
        <Form
          onSubmit={handleSubmit(onSubmit)}
        >
          <FormLayout>
            <h2>
              Option ...{index}
              <Icon
  source={DeleteIcon}
/>
            </h2>
            <Controller
              as={
                <TextField
                  label={'Title'}
                  onChange={([value]) => value}
                  error={errors && errors.title && errors.title.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                console.log('form state is', formState)
                return value
              }}
              name={"title"}
              control={control}
            />
            <Controller
              as={
                <TextField
                  label={'Quantity'}
                  onChange={([value]) => value}
                  error={errors && errors.quantity && errors.quantity.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                return value
              }}
              name={"quantity"}
              control={control}
            />
          </FormLayout>

        </Form>
      </div>
  );
};

export default ListOption;
