import React, { useState, useCallback } from "react";
import { FormLayout, Form, TextField, Select, Icon } from "@shopify/polaris";
import { useForm, Controller, ErrorMessage } from "react-hook-form";
import * as yup from "yup";
import {
  DeleteIcon
} from '@shopify/polaris-icons';

export interface Props {
  quantity: string;
  index: string;
}

const ListOption: React.FC<Props> = ({ quantity, index }) => {
  const [data, setData] = useState(null);

  const [selected, setSelected] = useState('0');
  const [huan, setHuan] = useState('0');

  const handleSelectChange = useCallback(
    (value: string) => {
      setSelected(value);
      setHuan(value);
      console.log('selected ok chu', value);

    },
    [],
  );

  const options = [
    { label: 'None', value: '0' },
    { label: '% Discount', value: '1' },
    { label: 'Discount/Each', value: '2' },
  ];

  const SignupSchema = yup.object().shape({
    campaign: yup.string().required(),
  });

  const onSubmit = (data: any) => {
    alert(JSON.stringify(data));
  };

  const defaultValues = {
    quantity: "",
    title: '',
    // description: ''
  };

  const { register, handleSubmit, errors, control, formState } = useForm({
    defaultValues,
    validationSchema: SignupSchema,
    mode: 'onChange',
    reValidateMode: 'onSubmit',
    validateCriteriaMode: 'firstError'
  });

  return (
    <div style={{ borderTop: '1px solid' }}>
      <Form
        onSubmit={handleSubmit(onSubmit)}
      >
        <FormLayout>
          <h2>
            Option ...{index}
            <Icon
              source={DeleteIcon}
            />
          </h2>
          <FormLayout.Group>
            <Controller
              as={
                <TextField
                  label={'Title'}
                  onChange={([value]) => value}
                  error={errors && errors.title && errors.title.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                console.log('form state is', formState)
                return value
              }}
              name={"title"}
              control={control}
            />
            <Controller
              as={
                <TextField
                  label={'Subtitle'}
                  onChange={([value]) => value}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                return value
              }}
              name={"subtitle"}
              control={control}
            />
            <Controller
              as={
                <TextField
                  label={'Label (optional)'}
                  onChange={([value]) => value}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                return value
              }}
              name={"label"}
              control={control}
            />
          </FormLayout.Group>

          <FormLayout.Group>
            <Controller
              as={
                <TextField
                  label={'Quantity'}
                  type="number"
                  autoComplete="off"
                  onChange={([value]) => value}
                  error={errors && errors.quantity && errors.quantity.message}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                return value
              }}
              name={"quantity"}
              control={control}
            />
            <Controller
              as={
                <Select
                  label='Discount Type'
                  options={options}
                  onChange={handleSelectChange}
                  value={selected}
                />
              }
              onChange={([value]) => {
                console.log('in change', value, errors)
                console.log('form state is', formState)
                return value
              }}
              name={"type"}
              control={control}
            />

            {/* //   <Controller
            //   as={
            //     <TextField
            //       label={'Amount'}
            //       type="number"
            //       autoComplete="off"
            //       suffix={selected === '1' ? '%' : '$'}
            //       onChange={([value]) => value}
            //       error={errors && errors.amount && errors.amount.message}
            //     />
            //   }
            //   onChange={([value]) => {
            //     console.log('in change', value, errors)
            //     console.log('form state is', formState)
            //     return value
            //   }}
            //   name={"amount"}
            //   control={control}
            // /> */}
            <div>{huan}</div>
          </FormLayout.Group>
        </FormLayout>
      </Form>
    </div>
  );
};

export default ListOption;
